container transfer {
  description
    "Configuration associated with the timing
     of a transfer.";

  choice how {
    default manual;
    description
      "Transfer timing methods.";

    case interval {
      leaf interval {
        type uint16;
        units minutes;
        default 30;
        description
          "Transfer every 'interval' minutes.";
      }
    }

    case manual {
      leaf manual {
        type empty;
        description
          "Only transfer when manually triggered.";
      }
    }
  }
}

